import React, { useState } from 'react';
import './App.css';
import SideBar from './components/SideBar.jsx';
import ChatPanel from './components/ChatPanel.jsx';
import SignUp from './components/SignUp.jsx';
import { BrowserRouter, Routes, Route } from "react-router-dom";

function App() {
  const [messages, setMessages] = useState([]);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [selectedUser, setSelectedUser] = useState("");

  return (
    <BrowserRouter>
      <div className="App">
        <Routes>
          {/* Signup route */}
          <Route path="/signUp" element={<SignUp username={username} setUsername={setUsername} password={password} setPassword={setPassword} />} />

          {/* Chat route */}
          <Route
            path="/chat"
            element={
              <div className="chatUI">
                <SideBar messages={messages} setMessages={setMessages} selectedUser={selectedUser}
                  setSelectedUser={setSelectedUser} />
                <ChatPanel messages={messages} setMessages={setMessages} username={username} selectedUser={selectedUser}
                  setSelectedUser={setSelectedUser} />
              </div>
            }
          />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
