import React, { useState, useEffect } from "react";
import "./ChatPanel.css";
import axios from "axios";

function ChatPanel({
  messages,
  setMessages,
  username,
  selectedUser,
  setSelectedUser,
}) {
  const [text, setText] = useState("");

  const currTime = new Date().toLocaleTimeString([], {
    hour: "2-digit",
    minute: "2-digit",
  });
  useEffect(() => {
    const chatBody = document.querySelector(".chatBody");
    chatBody.scrollTop = chatBody.scrollHeight;
    async function logMessage() {
      const lastMessage = messages[messages.length - 1];
      if (!lastMessage) return;
      await axios.post("http://localhost:3000/chatMessages", {
        sender: localStorage.getItem("username"),
        receiver: localStorage.getItem("selectedUser"),
        message: messages[messages.length - 1],
      });
    }
    logMessage();
  }, [messages]);
  return (
    <>
      <div className="chatPanel">
        <div className="chatPanelText">
          {" "}
          <span>{localStorage.getItem("selectedUser")}</span> Chat Panel
        </div>
        <div className="chatBody">
          {messages.map((message, index) => (
            <div className="chatMessage" key={index}>
              {message.message}
              <span className="currentTime">{message.time}</span>
            </div>
          ))}
        </div>
        <div className="chat">
          <input
            className="chatInput"
            type="text"
            value={text}
            onChange={(e) => {
              setText(e.target.value);
            }}
            onKeyDown={(e) => {
              if (e.key === "Enter" && text.length >= 1) {
                setMessages([...messages, { message: text, time: currTime }]);
                setText("");
              }
            }}
          />
          <button
            onClick={() => {
              if (text.length >= 1) {
                setMessages([...messages, { message: text, time: currTime }]);
                setText("");
              }
            }}
          >
            Enter
          </button>
        </div>
      </div>
    </>
  );
}
export default ChatPanel;
