import React, { useState, useEffect } from "react";
import "./SideBar.css";
import axios from "axios";

function SideBar({ messages, setMessages }) {
  const [search, setSearch] = useState(null);
  const [userData, setUserData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedUser, setSelectedUser] = useState("");
  const [localUsername, setLocalUsername] = useState("");

  useEffect(() => {
    async function fetchUserData() {
      try {
        const response = await axios.get("http://localhost:8080/users");
        const users = response.data.map((user) => user.username);
        setUserData(users);
        setLoading(!loading);
        console.log(users);
      } catch (e) {
        console.log("Error Fetching Users from DB:", e);
      }
    }
    fetchUserData();
    setLocalUsername(localStorage.getItem("username"));
  }, []);

  async function getMessages() {
    try {
      const response = await axios.get(
        `/chatMessages/${localStorage.getItem(
          "username"
        )}/${localStorage.getItem("selectedUser")}`
      );
      if (response.body.length < 1) {
        return [];
      }
      return response.body;
    } catch (e) {
      console.log(e);
    }
  }

  return (
    <>
      {loading ? (
        "Loading..."
      ) : (
        <div className="sideBar">
          <div className="chatApp">Chat App</div>
          <hr />
          <span className="username">{localUsername}</span>
          <div className="searchBar">
            <span className="searchBarText">Search Bar</span>
            <input
              className="sideBarInput"
              onChange={(e) => {
                setSearch(e.target.value);
              }}
            />
          </div>
          {search === null ? (
            <div>
              {userData.map((user, index) => (
                <div key={index}>
                  {" "}
                  <button
                    onClick={() => {
                      setSelectedUser(user);
                      localStorage.setItem("selectedUser", user);
                      setMessages(getMessages());
                      //   setMessages([]);
                    }}
                  >
                    {user}
                  </button>{" "}
                </div>
              ))}
            </div>
          ) : (
            <div>
              {userData
                .filter((filteredUser) =>
                  filteredUser.toLowerCase().includes(search.toLowerCase())
                )
                .map((user, index) => (
                  <div key={index}>
                    <button
                      onClick={() => {
                        setSelectedUser(user);
                        localStorage.setItem("selectedUser", user);
                        setMessages(getMessages());
                        // setMessages([]);
                      }}
                    >
                      {user}
                    </button>
                  </div>
                ))}
            </div>
          )}
        </div>
      )}
    </>
  );
}

export default SideBar;
